import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';


const Detail = () => {
    const { id } = useParams();
    const [character, setCharacter] = useState({});
    useEffect(() => {
        axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
            if (data.name) {
                setCharacter(data);
            } else {
                window.alert('No hay personajes con ese ID');
            }
        });
        return setCharacter({});
    }, [id]);

    return (  
        <>
        <div>
                

                    <h1 >{character.name}</h1>
                
                <h2 className={style.card__features}>{character.status}</h2>
                <h2 className={style.card__features}>{character.species}</h2>
                <h2 className={style.card__features}>{character.gender}</h2>
                {/* <h2 className={style.card__features}>{character.origin.name}</h2> */}

                <img src={character.image} alt={`${character.name}`} />
        </div>
        </>
    );
}
 
export default Detail;
